mapper.update:
UPDATE sys_user
SET
nickname = #{nickname}

    , password = #{password}


, role_id = #{roleId}
, delete_status = #{deleteStatus}
WHERE id = #{userId} and id != 10001

UPDATE sys_role_permission
SET
delete_status = '2'
WHERE role_id = #{roleId}
AND permission_id in (

    #{item}

)

UPDATE sys_role
SET
    role_name = #{roleName}
WHERE id = #{roleId}

mapper.select:
select count(0)
 from sys_user
 WHERE username=#{username}
 AND delete_status='1';
